import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "./ui/table";
import { Button } from "./ui/button";
import { FileIcon, Trash2Icon, Plus, Edit } from "lucide-react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "./ui/dialog";
import CertificateForm from './CertificateForm';

const API_URL = 'http://localhost:5001/api';

const CertificateTable = () => {
  const [certificates, setCertificates] = useState([]);
  const [editingCertificate, setEditingCertificate] = useState(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  useEffect(() => {
    fetchCertificates();
  }, []);

  const fetchCertificates = async () => {
    try {
      const response = await axios.get(`${API_URL}/certificates`);
      setCertificates(response.data);
    } catch (error) {
      console.error('Error fetching certificates:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/certificates/${id}`);
      fetchCertificates();
    } catch (error) {
      console.error('Error deleting certificate:', error);
    }
  };

  const handleEdit = (certificate) => {
    setEditingCertificate(certificate);
    setIsDialogOpen(true);
  };

  const handleFormClose = () => {
    setEditingCertificate(null);
    setIsDialogOpen(false);
    fetchCertificates();
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-GB');
  };

  return (
    <div className="space-y-4">
      <div className="flex justify-between items-center">
        <h2 className="text-xl font-semibold">Certificates</h2>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button className="bg-blue-500 hover:bg-blue-600 text-white">
              <Plus className="w-4 h-4 mr-2" />
              Add Certificate
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-[600px]">
            <DialogHeader>
              <DialogTitle>
                {editingCertificate ? 'Edit Certificate' : 'Add New Certificate'}
              </DialogTitle>
            </DialogHeader>
            <CertificateForm 
              onSubmitSuccess={handleFormClose}
              editData={editingCertificate}
            />
          </DialogContent>
        </Dialog>
      </div>

      <div className="rounded-md border">
        <Table>
          <TableHeader>
            <TableRow>
              <TableHead>Type</TableHead>
              <TableHead>Issuer</TableHead>
              <TableHead>At</TableHead>
              <TableHead>On</TableHead>
              <TableHead>Last Annual</TableHead>
              <TableHead>Expiry</TableHead>
              <TableHead>Status</TableHead>
              <TableHead>Certificate No</TableHead>
              <TableHead>Documents</TableHead>
              <TableHead className="text-right">Actions</TableHead>
            </TableRow>
          </TableHeader>
          <TableBody>
            {certificates.map((cert) => (
              <TableRow key={cert.id}>
                <TableCell>{cert.type}</TableCell>
                <TableCell className="relative">
                  {cert.issuer}
                  <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                    c11
                  </span>
                </TableCell>
                <TableCell className="relative">
                  {cert.at_location}
                  <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                    c12
                  </span>
                </TableCell>
                <TableCell className="relative">
                  {formatDate(cert.on_date)}
                  <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                    c13
                  </span>
                </TableCell>
                <TableCell className="relative">
                  {cert.last_annual ? formatDate(cert.last_annual) : '-'}
                  <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                    c14
                  </span>
                </TableCell>
                <TableCell className="relative">
                  {formatDate(cert.expiry_date)}
                  <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                    c15
                  </span>
                </TableCell>
                <TableCell className="relative">
                  {cert.status}
                  <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                    c16
                  </span>
                </TableCell>
                <TableCell>
                  <div className="flex items-center justify-between">
                    <span>{cert.certificate_no || '-'}</span>
                    <span className="text-xs text-orange-500">c17</span>
                  </div>
                </TableCell>
                <TableCell>
                  <Button variant="ghost" size="icon">
                    <FileIcon className="h-4 w-4 text-blue-500" />
                  </Button>
                </TableCell>
                <TableCell>
                  <div className="flex justify-end gap-2">
                    <Button 
                      variant="ghost"
                      onClick={() => handleEdit(cert)}
                    >
                      <Edit className="h-4 w-4 mr-1" />
                      Edit
                    </Button>
                    <Button 
                      variant="ghost" 
                      onClick={() => handleDelete(cert.id)}
                      className="text-red-500 hover:text-red-700"
                    >
                      <Trash2Icon className="h-4 w-4" />
                    </Button>
                  </div>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </div>
    </div>
  );
};

export default CertificateTable;
