import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "./ui/table";
import { Button } from "./ui/button";
import { FileIcon, Trash2Icon, Plus } from "lucide-react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "./ui/dialog";
import CertificateForm from './CertificateForm';

const API_URL = 'http://localhost:3000/api';

const CertificateTable = () => {
  const [certificates, setCertificates] = useState([]);
  const [editingCertificate, setEditingCertificate] = useState(null);
  const [isDialogOpen, setIsDialogOpen] = useState(false);

  useEffect(() => {
    fetchCertificates();
  }, []);

  const fetchCertificates = async () => {
    try {
      const response = await axios.get(`${API_URL}/certificates`);
      setCertificates(response.data);
    } catch (error) {
      console.error('Error fetching certificates:', error);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`${API_URL}/certificates/${id}`);
      fetchCertificates();
    } catch (error) {
      console.error('Error deleting certificate:', error);
    }
  };

  const handleEdit = (certificate) => {
    setEditingCertificate(certificate);
    setIsDialogOpen(true);
  };

  const handleFormClose = () => {
    setEditingCertificate(null);
    setIsDialogOpen(false);
    fetchCertificates();
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-GB');
  };

  return (
    <div className="p-4">
      <div className="mb-4 flex justify-between items-center">
        <h2 className="text-xl font-bold">Certificates</h2>
        <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="mr-2 h-4 w-4" />
              Add Certificate
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>
                {editingCertificate ? 'Edit Certificate' : 'Add New Certificate'}
              </DialogTitle>
            </DialogHeader>
            <CertificateForm 
              onSubmitSuccess={handleFormClose}
              editData={editingCertificate}
            />
          </DialogContent>
        </Dialog>
      </div>

      <Table>
        <TableHeader>
          <TableRow>
            <TableHead>Type</TableHead>
            <TableHead>Issuer</TableHead>
            <TableHead>At</TableHead>
            <TableHead>On</TableHead>
            <TableHead>Last Annual</TableHead>
            <TableHead>Expiry</TableHead>
            <TableHead>Status</TableHead>
            <TableHead>Certificate No</TableHead>
            <TableHead>Documents</TableHead>
            <TableHead>Actions</TableHead>
          </TableRow>
        </TableHeader>
        <TableBody>
          {certificates.map((cert) => (
            <TableRow key={cert.id}>
              <TableCell>{cert.type}</TableCell>
              <TableCell className="relative">
                {cert.issuer}
                <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                  {cert.issuer_tag}
                </span>
              </TableCell>
              <TableCell className="relative">
                {cert.at_location}
                <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                  {cert.at_location_tag}
                </span>
              </TableCell>
              <TableCell className="relative">
                {formatDate(cert.on_date)}
                <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                  {cert.on_date_tag}
                </span>
              </TableCell>
              <TableCell className="relative">
                {cert.last_annual ? formatDate(cert.last_annual) : '-'}
                <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                  {cert.last_annual_tag}
                </span>
              </TableCell>
              <TableCell className="relative">
                {formatDate(cert.expiry_date)}
                <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                  {cert.expiry_tag}
                </span>
              </TableCell>
              <TableCell className="relative">
                {cert.status}
                <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                  {cert.status_tag}
                </span>
              </TableCell>
              <TableCell className="relative">
                {cert.certificate_no || '-'}
                <span className="absolute bottom-1 right-1 text-xs text-orange-500">
                  {cert.certificate_no_tag}
                </span>
              </TableCell>
              <TableCell>
                <div className="flex gap-2">
                  <Button variant="ghost" size="icon">
                    <FileIcon className="h-4 w-4 text-blue-500" />
                  </Button>
                  <Button 
                    variant="ghost" 
                    size="icon"
                    onClick={() => handleDelete(cert.id)}
                  >
                    <Trash2Icon className="h-4 w-4 text-red-500" />
                  </Button>
                </div>
              </TableCell>
              <TableCell>
                <Button 
                  variant="ghost"
                  onClick={() => handleEdit(cert)}
                >
                  Edit
                </Button>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </div>
  );
};

export default CertificateTable;
